pub enum AddMappingError {
    InvalidMapping(NulError),
    InvalidFilePath(String),
    ReadError(String),
    SdlError(String),
}

pub enum DisplayEvent {
    None,
    Orientation(Orientation),
    Connected,
    Disconnected,
}

pub enum Event {
    Quit {
            timestamp: u32,
        },
    AppTerminating {
            timestamp: u32,
        },
    AppLowMemory {
            timestamp: u32,
        },
    AppWillEnterBackground {
            timestamp: u32,
        },
    AppDidEnterBackground {
            timestamp: u32,
        },
    AppWillEnterForeground {
            timestamp: u32,
        },
    AppDidEnterForeground {
            timestamp: u32,
        },
    Display {
            timestamp: u32,
            display_index: i32,
            display_event: DisplayEvent,
        },
    Window {
            timestamp: u32,
            window_id: u32,
            win_event: WindowEvent,
        },
    KeyDown {
            timestamp: u32,
            window_id: u32,
            keycode: Option<Keycode>,
            scancode: Option<Scancode>,
            keymod: Mod,
            repeat: bool,
        },
    KeyUp {
            timestamp: u32,
            window_id: u32,
            keycode: Option<Keycode>,
            scancode: Option<Scancode>,
            keymod: Mod,
            repeat: bool,
        },
    TextEditing {
            timestamp: u32,
            window_id: u32,
            text: String,
            start: i32,
            length: i32,
        },
    TextInput {
            timestamp: u32,
            window_id: u32,
            text: String,
        },
    MouseMotion {
            timestamp: u32,
            window_id: u32,
            which: u32,
            mousestate: MouseState,
            x: i32,
            y: i32,
            xrel: i32,
            yrel: i32,
        },
    MouseButtonDown {
            timestamp: u32,
            window_id: u32,
            which: u32,
            mouse_btn: MouseButton,
            clicks: u8,
            x: i32,
            y: i32,
        },
    MouseButtonUp {
            timestamp: u32,
            window_id: u32,
            which: u32,
            mouse_btn: MouseButton,
            clicks: u8,
            x: i32,
            y: i32,
        },
    MouseWheel {
            timestamp: u32,
            window_id: u32,
            which: u32,
            x: i32,
            y: i32,
            direction: MouseWheelDirection,
        },
    JoyAxisMotion {
            timestamp: u32,
            which: u32,
            axis_idx: u8,
            value: i16,
        },
    JoyBallMotion {
            timestamp: u32,
            which: u32,
            ball_idx: u8,
            xrel: i16,
            yrel: i16,
        },
    JoyHatMotion {
            timestamp: u32,
            which: u32,
            hat_idx: u8,
            state: HatState,
        },
    JoyButtonDown {
            timestamp: u32,
            which: u32,
            button_idx: u8,
        },
    JoyButtonUp {
            timestamp: u32,
            which: u32,
            button_idx: u8,
        },
    JoyDeviceAdded {
            timestamp: u32,
            which: u32,
        },
    JoyDeviceRemoved {
            timestamp: u32,
            which: u32,
        },
    ControllerAxisMotion {
            timestamp: u32,
            which: u32,
            axis: Axis,
            value: i16,
        },
    ControllerButtonDown {
            timestamp: u32,
            which: u32,
            button: Button,
        },
    ControllerButtonUp {
            timestamp: u32,
            which: u32,
            button: Button,
        },
    ControllerDeviceAdded {
            timestamp: u32,
            which: u32,
        },
    ControllerDeviceRemoved {
            timestamp: u32,
            which: u32,
        },
    ControllerDeviceRemapped {
            timestamp: u32,
            which: u32,
        },
    FingerDown {
            timestamp: u32,
            touch_id: i64,
            finger_id: i64,
            x: f32,
            y: f32,
            dx: f32,
            dy: f32,
            pressure: f32,
        },
    FingerUp {
            timestamp: u32,
            touch_id: i64,
            finger_id: i64,
            x: f32,
            y: f32,
            dx: f32,
            dy: f32,
            pressure: f32,
        },
    FingerMotion {
            timestamp: u32,
            touch_id: i64,
            finger_id: i64,
            x: f32,
            y: f32,
            dx: f32,
            dy: f32,
            pressure: f32,
        },
    DollarGesture {
            timestamp: u32,
            touch_id: i64,
            gesture_id: i64,
            num_fingers: u32,
            error: f32,
            x: f32,
            y: f32,
        },
    DollarRecord {
            timestamp: u32,
            touch_id: i64,
            gesture_id: i64,
            num_fingers: u32,
            error: f32,
            x: f32,
            y: f32,
        },
    MultiGesture {
            timestamp: u32,
            touch_id: i64,
            d_theta: f32,
            d_dist: f32,
            x: f32,
            y: f32,
            num_fingers: u16,
        },
    ClipboardUpdate {
            timestamp: u32,
        },
    DropFile {
            timestamp: u32,
            window_id: u32,
            filename: String,
        },
    DropText {
            timestamp: u32,
            window_id: u32,
            filename: String,
        },
    DropBegin {
            timestamp: u32,
            window_id: u32,
        },
    DropComplete {
            timestamp: u32,
            window_id: u32,
        },
    AudioDeviceAdded {
            timestamp: u32,
            which: u32,
            iscapture: bool,
        },
    AudioDeviceRemoved {
            timestamp: u32,
            which: u32,
            iscapture: bool,
        },
    RenderTargetsReset {
            timestamp: u32,
        },
    RenderDeviceReset {
            timestamp: u32,
        },
    User {
            timestamp: u32,
            window_id: u32,
            type_: u32,
            code: i32,
            data1: *mut c_void,
            data2: *mut c_void,
        },
    Unknown {
            timestamp: u32,
            type_: u32,
        },
}

pub enum PrefPathError {
    InvalidOrganizationName(NulError),
    InvalidApplicationName(NulError),
    SdlError(String),
}

pub enum ShowMessageError {
    InvalidTitle(NulError),
    InvalidMessage(NulError),
    InvalidButton(NulError, i32),
    SdlError(String),
}

pub enum TextureValueError {
    WidthOverflows(u32),
    HeightOverflows(u32),
    WidthMustBeMultipleOfTwoForFormat(u32, PixelFormatEnum),
    SdlError(String),
}

pub enum UpdateTextureYUVError {
    PitchOverflows {
            plane: &'static str,
            value: usize,
        },
    InvalidPlaneLength {
            plane: &'static str,
            length: usize,
            pitch: usize,
            height: usize,
        },
    XMustBeMultipleOfTwoForFormat(i32),
    YMustBeMultipleOfTwoForFormat(i32),
    WidthMustBeMultipleOfTwoForFormat(u32),
    HeightMustBeMultipleOfTwoForFormat(u32),
    RectNotInsideTexture(Rect),
    SdlError(String),
}

pub enum WindowBuildError {
    HeightOverflows(u32),
    WidthOverflows(u32),
    InvalidTitle(NulError),
    SdlError(String),
}

pub enum IntegerOrSdlError {
    IntegerOverflows(&'static str, u32),
    SdlError(String),
}
