pub enum UnderlineOption {
    None,
    Underline,
    StippledUnderline,
    SquigglyUnderline,
}

pub enum ClassStyle {
    Spaced,
    SpacedPrefixed {
            prefix: &'static str,
        },
}

pub enum ParsingError {
    MissingMainContext,
    MissingContext(ContextId),
    BadMatchIndex(usize),
    UnresolvedContextReference(ContextReference),
}

pub enum ContextReference {
    Named(String),
    ByScope {
            scope: Scope,
            sub_context: Option<String>,
            with_escape: bool,
        },
    File {
            name: String,
            sub_context: Option<String>,
            with_escape: bool,
        },
    Inline(String),
    Direct(ContextId),
}

pub enum MatchOperation {
    Push(Vec<ContextReference>),
    Set(Vec<ContextReference>),
    Pop,
    None,
}
