pub enum CopyError<S, D> {
    OutOfSrcBounds {
            size: S,
            copy_end: S,
        },
    OutOfDstBounds {
            size: D,
            copy_end: D,
        },
    Overlap {
            src_offset: usize,
            dst_offset: usize,
            size: usize,
        },
    NoSrcBindFlag,
    NoDstBindFlag,
}

pub enum UpdateError<T> {
    OutOfBounds {
            target: T,
            source: T,
        },
    UnitCountMismatch {
            target: usize,
            slice: usize,
        },
    InvalidUsage(Usage),
}

pub enum PipelineStateError<S> {
    Program(ProgramError),
    DescriptorInit(pso::InitError<S>),
    DeviceCreate(CreationError),
}

pub enum IndexBuffer<R: Resources> {
    Auto,
    Index16(handle::Buffer<R, u16>),
    Index32(handle::Buffer<R, u32>),
}

pub enum ElementError<S> {
    NotFound(S),
    Offset {
            name: S,
            shader_offset: c::pso::ElemOffset,
            code_offset: c::pso::ElemOffset,
        },
    Format {
            name: S,
            shader_format: c::shade::ConstFormat,
            code_format: c::shade::ConstFormat,
        },
}
