pub enum Event {
    WindowEvent {
            window_id: WindowId,
            event: WindowEvent,
        },
    DeviceEvent {
            device_id: DeviceId,
            event: DeviceEvent,
        },
    Awakened,
    Suspended(bool),
}

pub enum WindowEvent {
    Resized(u32, u32),
    Moved(i32, i32),
    Closed,
    DroppedFile(PathBuf),
    HoveredFile(PathBuf),
    HoveredFileCancelled,
    ReceivedCharacter(char),
    Focused(bool),
    KeyboardInput {
            device_id: DeviceId,
            input: KeyboardInput,
        },
    MouseMoved {
            device_id: DeviceId,
            position: (f64, f64),
        },
    MouseEntered {
            device_id: DeviceId,
        },
    MouseLeft {
            device_id: DeviceId,
        },
    MouseWheel {
            device_id: DeviceId,
            delta: MouseScrollDelta,
            phase: TouchPhase,
        },
    MouseInput {
            device_id: DeviceId,
            state: ElementState,
            button: MouseButton,
        },
    TouchpadPressure {
            device_id: DeviceId,
            pressure: f32,
            stage: i64,
        },
    AxisMotion {
            device_id: DeviceId,
            axis: AxisId,
            value: f64,
        },
    Refresh,
    Touch(Touch),
}

pub enum DeviceEvent {
    Added,
    Removed,
    Motion {
            axis: AxisId,
            value: f64,
        },
    Button {
            button: ButtonId,
            state: ElementState,
        },
    Key(KeyboardInput),
    Text {
            codepoint: char,
        },
}
